import React, { useState } from 'react'
import './Auth.css';
import Logo from '../../img/logo.png';

const Auth = () => {

    const [isSignUp, setIsSignUp] = useState(true);
    const [data , setData] = useState({firstname : "" , lastname: "" ,username:"" , password: "" , confirmpass: ""});

    const handleChange = (e) => {
        setData({...data, [e.target.name]: e.target.value})
    }

    return (
        <div className="Auth">
            {/* Left Side - Logo */}
            <div className="a-left">
                <img src={Logo} alt="" />
                <div className="Webname">
                    <h1>SK Media</h1>
                    <h6>Explore this ideas throughout the world</h6>
                </div>
            </div>
            {/* Right Side - SignUp */}
            <div className="a-right">
                <form action="" className="infoForm authForm">
                    <h3>{isSignUp ? "Sign Up" : "Login"}</h3>

                    {isSignUp &&
                        <div>
                            <input type="text" placeholder='First Name' className='infoInput' name='firstname' onChange={handleChange}/>
                            <input type="text" placeholder='Last Name' className='infoInput' name='lastname' onChange={handleChange}/>
                        </div>
                    }


                    <div>
                        <input type="text" className="infoInput" placeholder='User Name' name='username' onChange={handleChange}/>
                    </div>

                    <div>
                        <input type="password" className='infoInput' placeholder='Password' name='password' onChange={handleChange}/>
                        {isSignUp &&
                            <input type="password" className='infoInput' placeholder='Confirm Password' name='confirmpass' onChange={handleChange}/>
                        }
                    </div>

                    <div>
                        <span style={{ fontSize: "12px", cursor: "pointer" }} onClick={() => setIsSignUp((prev) => !prev)}>
                            {isSignUp ? "Already have an account. Login" : "Don't have an Account? SingUp"}
                        </span>
                    </div>
                    <button className="button infoButton" type='submit'>
                        {isSignUp ? "SignUp" : "Login"}
                    </button>
                </form>
            </div>
        </div>
    )
};


export default Auth
