const PostReducer = ({
    state  = {posts:null, loading: false , error: false , uploading:false}
    ,action
}) => {
    switch(action.type) {
        case "UPLOAD_START":
            return {...state , uploading: true, error: false}
        case "UPLOAD_SUCCESS":
            return {...state, posts: [action.data,...state],uploading:false,error:false}
        case "UPLOAD FAIL":
            return {...state, uploading:false,error:true}
        default:
            return false
    }
}


export default PostReducer
