import React, { useEffect, useState } from 'react'
import { getUser } from '../../api/UserReq';
import './Chatbox.css';
import { addMessage, getMessages } from '../../api/MessageRequest';
import { format } from 'timeago.js';
import InputEmoji from 'react-input-emoji'

const Chatbox = ({ chat, currentUser }) => {
    const [userData, setUserData] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState("");

    // fetching data for header 
    useEffect(() => {
        const userId = chat?.members?.find((id) => id !== currentUser);
        const getUserData = async () => {
            try {
                const { data } = await getUser(userId);
                setUserData(data);
            } catch (error) {
                console.log(error)
            }
        };
        if (chat !== null) getUserData();
    }, [chat, currentUser])

    // fetching data for messages
    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const { data } = await getMessages(chat._id);
                console.log(data)
                setMessages(data);
            } catch (err) {
                console.log(err);
            }
        }
        if (chat != null) fetchMessages();
    }, [chat])

    const handleChange = (newMessage) => {
        setNewMessage(newMessage)
    }

    const handleSend = async(e) => {
        e.preventDefault();
        const message = {
            senderId: currentUser,
            text: newMessage,
            chatId: chat._id,
        }

        // send message to database
        try {
            const {data} = await addMessage(message);
            setNewMessage([...messages,data]);
        } catch (error) {
            console.log(error);
        }
    }
    return (
        <>
            <div className="ChatBox-container">
                {chat ? (
                    <>
                        <div className="chat-header">
                            <div className="follower">
                                <div>
                                    <img src={userData?.profilePicture ? process.env.REACT_APP_PUBLIC_FOLDER + userData.profilePicture : process.env.REACT_APP_PUBLIC_FOLDER + 'defaultProfile.png'} alt='' className='followerImage'
                                        style={{ width: '50px', height: '50px' }}
                                    />
                                    <div className="name" style={{ fontSize: "0.8rem" }}>
                                        <span>{userData?.firstname} {userData?.lastname}</span>
                                    </div>
                                </div>
                            </div>
                            <hr style={{ width: '85%', border: '0.1px solid #ececece' }} />
                        </div>

                        {/*  chatbox messages*/}
                        <div className="chat-body">
                            {messages.map((message) => (
                                <>
                                    <div key={message._id} className={message.senderId === currentUser ? "message own" : "message"}>
                                        <span>{message.text}</span>
                                        <span>{format(message.createdAt)}</span>
                                    </div>
                                </>
                            ))}
                        </div>
                        {/* chat sender */}
                        <div className="chat-sender">
                            <div>+</div>
                            <InputEmoji value={newMessage} onChange={handleChange} />
                            <div className="send-button button" onClick={handleSend}>Send</div>
                        </div>
                    </>
                )
                : (
                    <span className='chatbox-empty-message'>Tap on a Chat to start Conversation...</span>
                )
                }

            </div>
        </>
    )
}

export default Chatbox
